import React, {useState} from "react";
import Card from "./Card"
import OperationBox from "./OperationBox";
import Parenthesis from "./Parenthesis";

function App(){
    const [operation, setOperation] = useState("");
    const [classNam,setClassNam]=useState('');
    const [isActive, setIsActive] = useState([true,false,false]);

    const [users, setUsers] = useState([
        {
          id: 1,
          text: "Joe",
          isClicked: false
        }
      ]);


    function arithmetics(event){
        const x = event.target.value;
        const y = event.target.id;
        console.log(y)
        if(isActive){
            setOperation(x);
        }
    }
    var isSelected = true;

    function findItem(id){
        handleClick(id);
    }

    function handleClick (id) {
        // 👇️ toggle isActive state on click
        console.log(this)
        setIsActive(current =>            
            current.id === id ?  true:false);
/*         setIsActive(current => {
            return current.filter((item,index) =>
            {return index !== id})

        }); */

      };




    return(
        <div>
            <h1>hi what is up</h1>
            <p>what is your name</p>
            <div className="height">
                <Parenthesis text ="("/>
                <Card />
                {/* <button className={isActive ? 'isSelected operation' : 'operation'} onClick={handleClick}text = {operation}></button> */}
                <OperationBox onClick={() => this.handleClick()} boolean={isActive} onChecked = {findItem} text = {operation} id = {1}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox boolean={isActive} onChecked = {findItem} text = {operation} id = {2}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox boolean={isActive} onChecked = {findItem} text = {operation} id = {3}/>
                <Card/>
                <Parenthesis text =")"/>
            </div>
            <br/>
            <br/>
            <div>
                <button onClick = {arithmetics} className='arith' value = "*">*</button>
                <button onClick = {arithmetics} className='arith' value = "/">/</button>
                <button onClick = {arithmetics} className='arith' value = "+">+</button>
                <button onClick = {arithmetics} className='arith' value = "-">-</button>
            </div>

        </div> 
    );
}

export default App;
