import React from "react";

function SubmitButton(props) {

    return (
        <div className={props.boolean ? "correct":"incorrect"}>
            <h1>{props.text}</h1>
            <button onClick={()=>{
            props.onEvaluate();
            props.setEvaluate(props.boolean);
            props.updateScore();
            }}>Enter</button>
        </div>
    )
}

export default SubmitButton
