import React, { useState } from "react";
import CardSuit from "./CardSuit";
import { useDrag, DragPreviewImage } from 'react-dnd';
import { Types } from "./Types";

function Card(props) {
  const img = "heart"

    const [collected, drag, dragPreview] = useDrag(() => ({
      type: Types.CARD,
      collect: (monitor) => ({
        opacity: monitor.isDragging() ? 0.4 : 1,
      }),
    }))
/*     return collected.isDragging ? (
      <div ref={dragPreview} />
    ) : (
      <div ref={drag} {...collected}>
        ...
      </div>
    ) */


  return (
    <div>
      <div ref = {drag} ref = {dragPreview} className="card grid-container">
        <div className="grid-item card-number">{props.number}</div>
        {Array.from({ length: 33 }, (_, i) =>
          <CardSuit key={i} image={img} id={i} number={props.number} />)}
        <div className="grid-item card-number backwards">{props.number}</div>
      </div>
    </div>  
  );
}

export default Card;
