import React, {useState} from "react";
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import axios from "axios";

function SubmitScore(props) {
    //await new Promise(r => setTimeout(r, 2000));
    //props.setIsFinished(false)
    const [inputText, setInputText] = useState("");

    const location = useLocation();
    function handleChange(event) {
        const newValue = event.target.value;
        setInputText(newValue);
    }
    score = location.state.score
    function onSubmit() {
        axios.post('http://localhost:8000/create', {
            name: inputText,
            score: score
        })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })

    }


    return (
        <React.Fragment>
            <h1>Submit your score here!</h1>
            <Link className="navbar-link" to="/game">Click here to play 24</Link>
            <p>{location.state.score}</p>

            <input placeholder="Your Name Here" onChange={handleChange} type="text" value={inputText} />
            <button onClick={onSubmit}>Submit Score</button>


        </React.Fragment>
    )

}

export default SubmitScore;
