import React, {useState} from "react";

function CardSuit(props){

    const image = () => {switch(props.image){
            case("club"): return require("../images/club.png");
            case("diamond"): return require("../images/diamond.png");
            case("heart"): return require("../images/heart.png");
            case("spade"): return require("../images/spade.png");
        }}

    const suitArrangement = () => {switch(props.number){
        case(1): return [16];
        case(2): return [6,26];
        case(3): return [16,26,6];
        case(4): return [5,7,25,27];
        case(5): return [16,5,7,25,27];
        case(6): return [5,7,25,27,15,17];
        case(7): return [5,7,25,27,15,17,11];
        case(8): return [5,7,25,27,15,17,11,21];
        case(9): return [16,5,7,25,27,10,12,20,22];
        case(10): return [5,7,25,27,10,12,20,22,11,21];
    }
    }

    return(
    <img src = {suitArrangement().includes(props.id)? image():""} className="grid-item" aria-hidden="true"/>
    )
}

export default CardSuit;
