import React, { useState } from "react";
import CardSuit from "./CardSuit";

function Card(props) {
  const img = "heart"

  function allowDrop(ev) {
    ev.preventDefault();
  }
  function drag(ev) {
    ev.dataTransfer.setData("text", ev.target.id);
  }

  function drop(ev) {
    ev.preventDefault();
    var data = ev.dataTransfer.getData("text");
    ev.target.appendChild(document.getElementById(data));
  }  

  return (

      <div className="card grid-container" draggable = "true">
        <div className="grid-item card-number">{props.number}</div>
        {Array.from({ length: 33 }, (_, i) =>
          <CardSuit key={i} image={img} id={i} number={props.number} />)}
        <div className="grid-item card-number backwards">{props.number}</div>
      </div>




  );
}

export default Card;
