import React, {useState} from "react";
import Card from "./Card"
import OperationBox from "./OperationBox";
import Parenthesis from "./Parenthesis";

function App(){
    const [operation, setOperation] = useState("");
    const [classNam,setClassNam]=useState('');
    const [isActive, setIsActive] = useState([true,false,false]);

    const [users, setUsers] = useState([
        {
          id: 1,
          text: "",
          isClicked: false
        },
        {
            id: 2,
            text: "",
            isClicked: false
        },
        {
            id: 3,
            text: "",
            isClicked: false
        }
      ]);
// boolean state to know if we are editing (this will let us display
const [isEditing, setIsEditing] = useState(false);
// object state to set so we know which todo item we are editing
const [currentUser, setCurrentUser] = useState({});

// function to get the value of the edit input and set the new state
function handleEditInputChange(e) {
    // set the new state value to what's currently in the edit input box
    setCurrentUser({ ...currentUser, name: e.target.value });
  }

  // function to handle when the "Edit user name" button is clicked
  function handleEditClick(user) {
    // set isEditing to true
    setIsEditing(true);
    // update the state to the updatedUsers
    setCurrentUser({ ...user });
  }

  function handleUpdateUser(id, updatedUser) {
    // here we are mapping over the users array - the idea is check if the user.id matches the id we pass into the function
    // if the id's match, use the second parameter to pass in the updated user object
    // otherwise just use old user
    const updatedObject = users.map((user) =>
      user.id === id ? updatedUser : user
    );
    // set editing to false because this function will be used inside an onSubmit function - which means the data was submited and we are no longer editing
    setIsEditing(false);
    // update the users state with the updated user
    setUsers(updatedObject);
  }

    function arithmetics(event){
        const x = event.target.value;
        const y = event.target.id;
        console.log(y)
        if(isActive){
            setOperation(x);
        }
    }
    var isSelected = true;

    function findItem(id){
        handleClick(id);
    }

    function handleClick (id) {
        // 👇️ toggle isActive state on click
        console.log(this)
        setIsActive(current =>            
            current.id === id ?  true:false);
/*         setIsActive(current => {
            return current.filter((item,index) =>
            {return index !== id})

        }); */

      };




    return(
        <div>
            <h1>hi what is up</h1>
            <p>what is your name</p>
            <div className="height">
                <Parenthesis text ="("/>
                <Card />
                {/* <button className={isActive ? 'isSelected operation' : 'operation'} onClick={handleClick}text = {operation}></button> */}
                <OperationBox onClick={() => this.handleClick()} boolean={isActive} onChecked = {findItem} text = {operation} id = {1}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox boolean={isActive} onChecked = {findItem} text = {operation} id = {2}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox boolean={isActive} onChecked = {findItem} text = {operation} id = {3}/>
                <Card/>
                <Parenthesis text =")"/>
            </div>
            <br/>
            <br/>
            <div>
                <button onClick = {arithmetics} className='arith' value = "*">*</button>
                <button onClick = {arithmetics} className='arith' value = "/">/</button>
                <button onClick = {arithmetics} className='arith' value = "+">+</button>
                <button onClick = {arithmetics} className='arith' value = "-">-</button>
            </div>

        </div> 
    );
}

export default App;
