import React from "react";
import { Link } from "react-router-dom";
import GameSection from "./GamePage/GameSection";

function Instructions() {
    let randFour = [10, 5, 4, 4];
    let randCard = ["spade", "spade", "spade", "heart"]
    return (
        <div>
            <br />
            <h1>Instructions</h1>
            <ol>
                <li>The goal of the game is to use the four cards to make the number 24 by using addition, subtraction, multiplication, and division. </li>
            </ol>

            <br />
            <div className="bottom">
                <GameSection randFour={randFour} randCard={randCard} />
                <Link to="/game">PLAY</Link>
            </div>

        </div>
    )
}

export default Instructions
