import React, { useState, useEffect } from "react";// {useState}
import GameSection from "./GameSection";
import Leaderboard from "./Leaderboard";
import axios from "axios";

function App() {

    const [randFour, setRandFour] = useState([1, 2, 3, 4]);
    const [randCard, setRandCard] = useState(["spade", "heart", "club", "diamond"]);
    const [score, setScore] = useState(0);
    const [evaluate, setEvaluate] = useState(null);
    const [nameList, setNameList] = useState("");
    const [scoreList, setScoreList] = useState("");
    const [isHidden, setIsHidden] = useState(true);
    const [inputText, setInputText] = useState("");
    //functions from node
    useEffect(() => {
        fetch("http://localhost:8000/message")
            .then((res) => res.json())
            .then((data) => { setRandFour(data.randFour); setRandCard(data.randCard) });
    }, []);

    //data from mongoDB->node->react
    useEffect(() => {
        fetch("http://localhost:8000/home")
            .then((res) => res.json())
            .then((dataSet) => {
                let tempNameList = []
                let tempScoreList = []
                dataSet.forEach(data => {
                    tempNameList.push(data.name);
                    tempScoreList.push(data.score);
                })
                setNameList(tempNameList);
                setScoreList(tempScoreList);
            });
    }, []);

    function onSubmit(event) {
        //can also use axios
        axios.post('http://localhost:8000/create', { 
            name: inputText,
            score: score
             })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })


    /*         console.log("fetching")
            fetch('http://localhost:8000/create', {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json"},

                mode: 'cors',
                // We convert the React state to JSON and send it as the POST body
                body: JSON.stringify({
                    name: "inputText",
                    score: 14
                })
            }).then(response => response.json());
            event.preventDefault(); */
}


//need to fix -> post evaluate to get the correct value 
function updateScore() {
    if (evaluate) {
        setScore(score + 100);
    }
    else {
        setScore(score - 100);
    }
}

//console.log(evaluate);
function changeVisibility() {
    setIsHidden(!isHidden)
}

function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
}
return (
    <div>
        <div style={{ display: "inline-flex" }}>
            <h1> Score: {score}</h1>
        </div>

        <GameSection randFour={randFour} setEvaluate={setEvaluate} randCard={randCard} updateScore={updateScore} />
        <input placeholder="Your Name Here" onChange={handleChange} type="text" value={inputText} />
        <button onClick={onSubmit}>Submit Score</button>
        {/* <h1>{randFour}</h1> */}

        <button onClick={changeVisibility}>View Leaderboard</button>
        <Leaderboard name={nameList} score={scoreList} isHidden={isHidden} />

    </div>
);
}

export default App;
