import React, {useState} from "react";
import Card from "./Card"
import OperationBox from "./OperationBox";
import Parenthesis from "./Parenthesis";

function App(){
    const [operation, setOperation] = useState("");
    const [classNam,setClassNam]=useState('');

    function arithmetics(event){
        const x = event.target.value;
        setOperation(x);
    }
    var isSelected = true;

    function toChange(){
        setClassNam("isSelected");
        console.log("hi");
    }


    return(
        <div>
            <h1>hi what is up</h1>
            <p>what is your name</p>
            <div className="height">
                <Parenthesis text ="("/>
                <Card/>
                <OperationBox classNa = {classNam} onClick={toChange} text = {operation}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox text = {operation}/>
                <Parenthesis text ="("/>
                <Card/>
                <Parenthesis text =")"/>
                <OperationBox text = {operation}/>
                <Card/>
                <Parenthesis text =")"/>
            </div>
            <br/>
            <br/>
            <div>
                <button onClick = {arithmetics} className='arith' value = "*">*</button>
                <button onClick = {arithmetics} className='arith' value = "/">/</button>
                <button onClick = {arithmetics} className='arith' value = "+">+</button>
                <button onClick = {arithmetics} className='arith' value = "-">-</button>
            </div>

        </div> 
    );
}

export default App;
