import React, { useState, useEffect } from "react";

function Timer(props) {

    const { initialMinute = '05', initialSeconds = '00' } = props;
    const [minutes, setMinutes] = useState(initialMinute);
    const [seconds, setSeconds] = useState(initialSeconds);
    const start = Date.now()
    useEffect(() => {
        let myInterval = setInterval(() => {
            let delta = Number(minutes)*60 +Number(seconds) - (Date.now()-start)/1000

            setSeconds((delta%60));
            setMinutes(Math.floor(delta/60));
/*             if (Number(seconds) > 0) {
                setSeconds(Number(seconds - 1).toLocaleString(undefined, { minimumIntegerDigits: 2 }));
            }
            if (Number(seconds) === 0) {
                if (Number(minutes) === 0) {
                    clearInterval(myInterval)
                } else {
                    setMinutes(Number(minutes - 1).toLocaleString(undefined, { minimumIntegerDigits: 2 }));
                    setSeconds('59');
                }
            } */
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });

    setInterval(function () {
        var delta = Date.now() - start; // milliseconds elapsed since start
        //console.log(Math.floor(delta / 1000)); // in seconds
        // alternatively just show wall clock time:
        //console.log(new Date().toUTCString());

    }, 1000); // update about every second

    return (
        <div >
            {minutes}:{seconds}
        </div>
    )
}

export default Timer
